#include<iostream>#define ll long longusing namespace std;int main(){    int t;    cin>>t;    while(t--){        string s;        cin>>s;        int n=s.length();        int count_0=0;        int count_1=0;        for(int i=0;i<n;i++){            if( s[i]=='0' ){                count_0++;            }            else{                count_1++;            }        }         int count= min(count_0,count_1);                if(count%2==0){            cout<<"NET\n";        }        else{            cout<<"DA\n";        }       }}