#include<iostream>#define ll long long using namespace std;bool prime_factors_other_than_2_and_3(ll n){    while(n%2==0){        n/=2;    }    while(n%3==0){        n/=3;    }    return n!=1;}int main(){    int t;    cin>>t;    while(t--){        ll n;        cin>>n;        if(prime_factors_other_than_2_and_3(n)){            cout<<-1<<endl;            continue;        }        ll count2=0,count3=0;        while(n%2==0){            n/=2;            count2++;        }        while(n%3==0){            n/=3;            count3++;        }        if(count2>count3){            cout<<-1<<endl;                    }        else{            cout<<2*count3 - count2<<endl;        }    }}