#include<iostream>#include<vector>#include<algorithm>#define ll long longusing namespace std;int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        int n ;        cin>>n;        vector<int>a(n);        for(int i=0;i<n;i++){            cin>>a[i];        }        bool possible = false;        for(int i=1;i<n-1;i++){            if(a[i]>a[i-1] && a[i]>a[i+1]){                cout<<"YES\n";                cout<<i<<" "<<i + 1<<" "<<i + 2<<"\n";                possible = true;                break;            }        }        if(!possible){            cout<<"NO\n";        }    }}