#include<iostream>#include<vector>#include<algorithm>#include<cmath>#define ll long longusing namespace std;int main(){    ios::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        int n,k;        cin>>n>>k;        vector<ll>a(n*k);        for(int i=0;i<n*k;i++){            cin>>a[i];        }                int no_of_elements_required=(n/2) + 1;                int no_of_elements_required_from_end= no_of_elements_required*k;        ll required_sum=0;        for(int i=((n*k)- no_of_elements_required_from_end); i< n*k; i+=no_of_elements_required){            required_sum+=a[i];        }        cout<<required_sum<<"\n";    }}