#include<iostream>#include<vector>#define ll long longusing namespace std;int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        ll n,m;        cin>>n>>m;        ll sum=0;        vector<vector<int>> a(n,vector<int>(m));            ll negative_cnt=0;        int min_abs=1e9;        for(int i=0;i<n;i++){            for(int j=0;j<m;j++){                cin>>a[i][j];                negative_cnt+=a[i][j]<0 ? 1 : 0;                min_abs=min(min_abs,abs(a[i][j]));                sum+=a[i][j]<0 ? -a[i][j] : a[i][j];            }        }        if(negative_cnt%2==0){        cout<<sum<<"\n";        }        else{            cout<<sum-2*min_abs<<"\n";        }    }}