#include<iostream>#include<vector>#include<algorithm>#include<cmath>#define ll long longusing namespace std;int main(){    int t;    cin>>t;    while(t--){        ll n,x;        cin>>n>>x;        vector<ll>a(n);        ll max_sum=0;        ll sum=0;        for(int i=0;i<n;i++){            cin>>a[i];            max_sum+= ceil((double)a[i]/x);            sum+=a[i];        }        ll min_sum= ceil((double)sum/x);                cout<<min_sum<<" "<<max_sum<<"\n";    }}