#include<iostream>#define ll long long using namespace std;int smallest_prime_greater(ll n){    bool possible=true;    for(ll i=2;i*i<=n;i++){        if(n%i==0){            possible=false;            break;        }    }    if(possible){        return n;    }    return smallest_prime_greater(n+1); }int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){         ll d;        cin>>d;         ll first_divisor= smallest_prime_greater(1+d);        ll second_divisor= smallest_prime_greater(first_divisor+d);        cout<<first_divisor*second_divisor<<"\n";    }}