#include<iostream>#include<climits>#define ll long long using namespace std;int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        ll a,b;        cin>>a>>b;        ll ans=INT_MAX;        for(int i=0;i<32;i++){            ll operations=i;            ll new_b=b+i;            if(new_b==1){                continue;            }            ll copy_a=a;            while(copy_a>0){                copy_a/=new_b;                operations++;            }            ans= min(ans,operations);        }        cout<<ans<<"\n";    }}