#include<iostream>#include<string>using namespace std;int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        string a,b;        cin>>a;        cin>>b;        int n=a.size();        int m=b.size();        int lcs=0;        for(int i=1;i<=min(n,m);i++){            for(int j=0;j+i<=n;j++){                string s1= a.substr(j,i);                for(int k=0;k+i<=m;k++){                    string s2= b.substr(k,i);                    if(s1==s2){                        lcs=max(lcs,i);                    }                }            }        }        cout<<n+m-2*lcs<<"\n";    }}