#include<iostream>#include<vector>#include<algorithm>#include<cmath>#define ll long longusing namespace std;int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        int n;        cin>>n;        vector<int> a(n);        ll sum=0;        ll count_1=0;        ll count_0=0;        for(int i=0;i<n;i++){            cin>>a[i];            sum+=a[i];            if(a[i]==1)count_1++;            else if(a[i]==0) count_0++;        }        if(count_1==0){            cout<<0<<"\n";        }                else {            ll ans= count_1 * pow(2,count_0);            cout<<ans<<"\n";        }    }}