#include<iostream>#include<string>#include<climits>#define ll long longusing namespace std;int cost(string &s,char a,char b){    int idx_1=-1,idx_2=-1;   for(int i=s.length()-1;i>=0;i--){    if(s[i]==b){        idx_2=i;        break;    }   }   if(idx_2==-1)return INT_MAX;    for(int i=idx_2-1;i>=0;i--){     if(s[i]==a){          idx_1=i;          break;     }    }    if(idx_1==-1)return INT_MAX;    return (s.length()-1-idx_2)+(idx_2-1-idx_1);}void solve(){    ll n;    cin>>n;    string s=to_string(n);        int ans= INT_MAX;    ans=min(ans,cost(s,'0','0'));    ans=min(ans,cost(s,'2','5'));    ans=min(ans,cost(s,'5','0'));    ans=min(ans,cost(s,'7','5'));Â     cout<<ans<<"\n";}int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        solve();    }}