#include <iostream> using namespace std; void solve() {    long long x0, n;    cin >> x0 >> n;     long long displacement = 0;    long long remainder = n % 4;    if (remainder == 0) {        displacement = 0;    } else if (remainder == 1) {        displacement = -n;    } else if (remainder == 2) {        displacement = 1;    } else if (remainder == 3) {        displacement = n + 1;    }     if (x0 % 2 == 0) {        cout << x0 + displacement <<"\n";    } else {        cout << x0 - displacement << "\n";    }} int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);     int t;    cin >> t;    while (t--) {        solve();    }    return 0;}