#include<iostream>#include<vector>#include<climits>#define ll long longusing namespace std;int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        ll w,h;        cin>>w>>h;        ll area= INT_MIN;        for(ll i=0;i<4;i++){            ll k;            cin>>k;            ll max_v;            ll min_v;            for(int j=0;j<k;j++){                ll x;                cin>>x;                if(j==0)min_v= x;                if(j==k-1) max_v= x;            }            ll base= max_v-min_v;            if(i<2) area= max(area,base*h);            else            area= max(area,base*w);        }                  cout<<area<<"\n";    }}