#include<iostream>#include<vector>using namespace std;bool check_if_power_of_2(int n){    return n>0 && (n & (n-1))==0;}int get_msb(int n) {    int msb = 1;    while (msb * 2 < n) {        msb *= 2;    }    return msb;}int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        int n;        cin>>n;        if(n==2){            cout<<0<<" "<<1<<"\n";        }        else if(check_if_power_of_2(n-1)){            for(int i=0;i<n-2;i++){                cout<<i+1<<" ";            }            cout<<0<<" "<<n-1<<"\n";        }        else{            int num= get_msb(n-1);            cout<<0<<" "<<num<<" ";            for(int i= num+2;i<=n-1;i++){                cout<<i<<" ";            }            cout<<num+1<<" "<<1<<" ";            for(int j=2;j<num;j++){                cout<<j<<" ";            }            cout<<"\n";        }    }}