#include<iostream>#include<string>using namespace std;int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        int n,r,b;        cin>>n>>r>>b;        int no_of_r_in_a_grp= r/(b+1);                int extra_r= r%(b+1);        for(int i=0;i<(b+1);i++){            int current_r= no_of_r_in_a_grp + (extra_r>0?1:0);            for(int j=0;j<current_r;j++){                cout<<"R";            }            if(i<b){                cout<<"B";            }            if(extra_r>0) extra_r--;        }        cout<<"\n";    }}