#include<iostream>#include<vector>#include<map>using namespace std;int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        int n;        cin>>n;        vector<int>a(n);        map<int,int>freq;        int max_freq=0;        for(int i=0;i<n;i++){            cin>>a[i];            freq[a[i]]++;            max_freq=max(max_freq,freq[a[i]]);        }                int count=max_freq;        int clones=0;        while(count<n){            clones++;            int swaps=min(count,n-count);            clones+=swaps;            count+=swaps;        }        cout<<clones<<"\n";Â     }}