#include<iostream>#include<string>#include<vector>#include<algorithm>#include<unordered_map> using namespace std; int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int n;    cin>>n;    while(n--){        string s,t;        cin>>s>>t;        vector<int>pos_t(26,0);        for(char c:t){            pos_t[c-'A']++;        }        for(int i=s.length()-1;i>=0;i--){           if(pos_t[s[i]-'A']>0){               pos_t[s[i]-'A']--;           }           else{               s[i]='*';           }        }        string res="";        for(char c:s){            if(c!='*'){                res+=c;            }        }        if(res==t){            cout<<"YES\n";        }        else{            cout<<"NO\n";        }    }}