#include<iostream>#include<vector>#include<algorithm>#define ll long longusing namespace std;int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        ll n;        cin>>n;        vector<ll>a(n);                for(int i=0;i<n;i++){            cin>>a[i];        }            int count=0;            bool impossible=false;            for(int i= n-2;i>=0;i--){                while(a[i]>=a[i+1]){                    if(a[i+1]==0){                        impossible=true;                        break;                    }                    a[i]=a[i]/2;                    count++;                }                    if(impossible){                        break;                    }                            }            if(impossible){                cout<<-1<<"\n";            }            else cout<<count<<"\n";            }}