#include<iostream>#define ll long long using namespace std;int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        ll n;        cin>>n;        if(n%2!=0 || n<4){            cout<<-1<<"\n";        }        else{            ll max_bus= n/4;            ll min_bus;            if(n%6==0){                min_bus= n/6;            }            else{                min_bus= (n/6)+1;            }            cout<<min_bus<<" "<<max_bus<<"\n";        }    }}