#include<iostream>#include<string>#include<set>#include<algorithm>using namespace std;int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        int n,k;        cin>>n>>k;        string s;        cin>>s;        int curr_w=0;        for(int i=0;i<k;i++){            if(s[i]=='W') curr_w++;        }        int min_w= curr_w;        for(int i=k;i<n;i++){            if(s[i-k]=='W') curr_w--;            if(s[i]=='W') curr_w++;            min_w= min(min_w,curr_w);        }        cout<<min_w<<"\n";    }}