#include<iostream>#include<set>#include<vector>#include<algorithm>#include<unordered_map>#define ll long longusing namespace std; int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        ll n;        cin>>n;        vector<ll> a(n);        for(ll i=0;i<n;i++){            cin>>a[i];        }        int i=0;        vector<ll>p(n);        while(i<n){            int j=i;            while(j<n && a[j]==a[i]){                j++;            }            int grp_size= j-i;            if(grp_size==1){                cout<<-1<<"\n";                break;            }            for(int k= i;k<j-1;k++){                p[k]=k+2;            }            p[j-1]=i+1;            i=j;         }        if(i==n){            for(int i=0;i<n;i++){                cout<<p[i]<<" ";            }            cout<<"\n";        }     }}