#include<iostream>#include<vector>#define ll long longusing namespace std;int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        ll n;        cin>>n;        vector<ll>a(n);        ll index_of_first_non_zero=-1;        ll index_of_last_non_zero=-1;        for(int i=0;i<n;i++){            cin>>a[i];            if(a[i]!=0){                if(index_of_first_non_zero==-1){                    index_of_first_non_zero=i;                }                index_of_last_non_zero=i;            }        }        if(index_of_first_non_zero==-1 && index_of_last_non_zero==-1){            cout<<0<<"\n";        }        else{            bool zeros_in_between=false;            for(int i=index_of_first_non_zero;i<=index_of_last_non_zero;i++){                if(a[i]==0){                    zeros_in_between=true;                    break;                }            }            if(zeros_in_between){                cout<<2<<"\n";            }            else{                cout<<1<<"\n";            }        }    }}