#include<iostream>#include<algorithm>#include<vector>#define ll long longusing namespace std;int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);Â     int t;    cin>>t;    while(t--){        ll n,x;        cin>>n>>x;        vector<ll>a(n);        vector<pair<ll,ll>>segment_tree;        for(int i=0;i<n;i++){            cin>>a[i];            segment_tree.push_back({(a[i] - x),(a[i] + x)});        }        ll ans=0;        ll l= segment_tree[0].first;        ll r= segment_tree[0].second;        for(int i=1;i<n;i++){            l= max(l, segment_tree[i].first);            r= min(r, segment_tree[i].second);            if(l>r){                ans++;                l= segment_tree[i].first;                r= segment_tree[i].second;            }        }        cout<<ans<<"\n";    }    }