#include<iostream>#include<vector>#include<algorithm>#define ll long longusing namespace std;int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        ll n,k,b,s;        cin>>n>>k>>b>>s;        if(s< b*k || s > b*k + n*(k-1)){            cout<<"-1\n";            continue;        }        vector<ll> a(n,0);        a[0]= b*k;        s-= b*k;        for(int i=0;i<n ;i++){            ll add= min(s,k-1);            a[i]+=add;            s-=add;        }        for(int i=0;i<n;i++){            cout<<a[i]<<" ";        }                cout<<"\n";    }}