#include<iostream>#include<vector>#include<algorithm>#define ll long longusing namespace std;int number_of_players_needed(ll d,ll x){    return ((d/x) + 1);}int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    ll n,d;    cin>>n>>d;    vector<ll> a(n);    for(ll i=0;i<n;i++){        cin>>a[i];    }    sort(a.begin(), a.end());    reverse(a.begin(), a.end());    ll wins=0;    ll used_players=0;    for(ll i=0;i<n;i++){        if(used_players>=n){            break;        }        ll players_needed=number_of_players_needed(d, a[i]);        if(used_players + players_needed<=n){            wins++;            used_players+=players_needed;        }        else{            break;        }            }    cout<<wins<<"\n";}