#include<iostream>#include<string>#include<algorithm>#define ll long longusing namespace std;int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        ll n;        char c;        cin>>n>>c;        string s;        cin>>s;        int index=-1;        if(c=='g'){            cout<<0<<"\n";            continue;        }        s+=s;        ll count=0;        ll nearest_index_g=-1;        for(ll i=2*n-1;i>=0;i--){            if(s[i]=='g'){                nearest_index_g=i;            }            if(s[i]==c){                if(nearest_index_g!=-1){                    count=max(count, nearest_index_g - i);            }        }    }        cout<<count<<"\n";    }}