#include<iostream>#include<vector>using namespace std; int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        int n;        cin>>n;        vector<int> a(n);        int count_of_2=0;        for(int i=0;i<n;i++){            cin>>a[i];            if(a[i]==2){                count_of_2++;            }        }Â         if(count_of_2%2!=0){            cout<<-1<<"\n";        }        else{            if(count_of_2==0){                cout<<1<<"\n";                continue;            }            int k= count_of_2/2;            int sum=0;            for(int i=0;i<n;i++){                if(a[i]==2){                    sum++;                    if(sum==k){                        cout<<i+1<<"\n";                        break;                }            }        }    } }}