#include<iostream>#include<string>#include<vector>#include<algorithm>#include<set>Â #define ll long longusing namespace std;int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    ll t;    cin>>t;    while(t--){        ll n;        cin>>n;        string s;        cin>>s;            set<char> distinct_chars;        vector<ll> prefix(n,0);        for(ll i=0;i<n;i++){            distinct_chars.insert(s[i]);            prefix[i]= distinct_chars.size();        }        distinct_chars.clear();        vector<ll> suffix(n,0);        for(int i=n-1;i>=0;i--){            distinct_chars.insert(s[i]);            suffix[i]= distinct_chars.size();        }                ll result=0;        for(int i=0;i<n-1;i++){            result= max(result, prefix[i] + suffix[i+1]);        }        cout<<result<<"\n";    }}