#include<iostream>#define ll long longusing namespace std;int main(){    int t;    cin>>t;    while(t--){        ll a,b,c,d;        cin>>a>>b>>c>>d;        if(d<b){            cout<<-1<<'\n';        }        else{            // k= steps needed to reach from b to d            ll k= d-b;            //after k steps a changes from a to a+k            a= a+k;            if(a>=c){                cout<<k + (a-c)<<'\n';            }            else{                cout<<-1<<'\n';            }        }    }}