#include<iostream>#include<vector>#include<algorithm>#define ll long longusing namespace std;void solve(){    int n,q;    cin>>n>>q;    vector<ll>arr(n);    vector<ll>prefix_sum(n+1,0);    for(int i=0;i<n;i++){        cin>>arr[i];        prefix_sum[i+1]=prefix_sum[i]+arr[i];    }    ll total_sum=prefix_sum[n];    while(q--){        int l,r,k;        cin>>l>>r>>k;        ll subarray_sum=prefix_sum[r]-prefix_sum[l-1];        ll new_sum=total_sum - subarray_sum + (ll)(r - l + 1)*k;        if(new_sum%2==1){            cout<<"YES"<<endl;        }        else{            cout<<"NO"<<endl;        }    } } int main(){    int t;    cin>>t;    while(t--){        solve();    }}