#include<iostream>#define ll long longusing namespace std;int main(){    int t;    cin>>t;    while(t--){        ll n;        ll k;        cin>>n>>k;        if(n%2!=0){            if(k%2==0){                cout<<"NO\n";            }            else{                if(n<k){                    cout<<"NO\n";                }                else{                    cout<<"YES\n";                }            }        }        else{            cout<<"YES\n";        }    }}