#include<iostream>#include<vector>#include<cmath>#define ll long longusing namespace std;ll gcd(ll a,ll b){    if(b==0){        return a;    }    return gcd(b,a%b);}int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        int n;        cin>>n;        vector<int> p(n);        ll ans=0;        for(int i=0;i<n;i++){            cin>>p[i];            ll diff=abs(p[i]- (i+1));            ans=gcd(ans,diff);        }        cout<<ans<<"\n";    }}