#include<iostream>#include<vector>using namespace std;int main(){    int t;    cin>>t;    while(t--){        int n;        cin>>n;        vector<int> a(n);        for(int i=0;i<n;i++) cin>>a[i];        int count_0=0;        for(int i=0;i<n;i++){            if(a[i]==0){                 int consecutive_count_0=1;                for(int j=i+1;j<n;j++){                    if(a[j]==0){                        consecutive_count_0++;                    }                    else{                        i=j;                        break;                    }                }                count_0= max(count_0,consecutive_count_0);            }        }        cout<<count_0<<"\n";    }}