#include<iostream>#include<vector>#define ll long longusing namespace std;int  main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    ll t;    cin>>t;    while(t--){        ll n;        cin>>n;        vector<ll> a(n),b(n);        for(int i=0;i<n;i++){            cin>>a[i];        }        for(int i=0;i<n;i++){            cin>>b[i];        }        vector<ll>longest_subarray_in_a(2*n+1,0);        vector<ll>longest_subarray_in_b(2*n+1,0);         ll cnt=1;        for(int i=0;i<n-1;i++){            if(a[i]==a[i+1]){                cnt++;            }            else{                longest_subarray_in_a[a[i]]= max(longest_subarray_in_a[a[i]],cnt);                cnt=1;            }        }        longest_subarray_in_a[a[n-1]]= max(longest_subarray_in_a[a[n-1]],cnt);        cnt=1;        for(int i=0;i<n-1;i++){            if(b[i]==b[i+1]){                cnt++;            }            else{                longest_subarray_in_b[b[i]]= max(longest_subarray_in_b[b[i]],cnt);                cnt=1;            }        }        longest_subarray_in_b[b[n-1]]= max(longest_subarray_in_b[b[n-1]],cnt);         ll result=0;        for(int i=1;i<=2*n;i++){            result= max(result, (longest_subarray_in_a[i] + longest_subarray_in_b[i]));        }        cout<<result<<"\n";    }}