#include<iostream>#include<vector>#include<algorithm>#include<unordered_map>using namespace std;int main(){    int t;    cin>>t;    while(t--){        int n;        cin>>n;        vector<int>a(n);                int count_2=0;        int sum=0;        for(int i=0;i<n;i++){            cin>>a[i];            sum+=a[i];            if(a[i]==1) continue;            else count_2++;        }        int total_op=0;        while(sum<0){            count_2--;            total_op++;            sum+=2;        }        if(count_2%2!=0){            cout<<total_op + 1<<endl;        }        else{            cout<<total_op<<endl;        }    }}