#include <iostream>using namespace std; void solve() {    int x, k;    cin >> x >> k;     // Case 1: If x is not divisible by k, we can jump directly    if (x % k != 0) {        cout << 1 << endl;        cout << x << endl;    }     // Case 2: If x IS divisible by k, we split it into (x-1) and 1    else {        cout << 2 << endl;        // Since x is a multiple of k, x-1 cannot be.        // And since k >= 2, 1 cannot be divisible by k.        cout << x - 1 << " " << 1 << endl;    }} int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);        int t;    cin >> t;    while (t--) {        solve();    }    return 0;}