#include<iostream>#include<string>using namespace std;int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        int n;        cin>>n;        string s;        cin>>s;        if(n==1){            cout<<2<<"\n";        }        else{            int max_count=1;            int current_length=1;            for(int i=1;i<n;i++){                if(s[i]==s[i-1]){                    current_length++;                }                else{                    current_length=1;                }                max_count=max(max_count,current_length);            }            cout<<max_count+1<<"\n";        }    }}