#include <iostream>using namespace std; int main() {    int t;    cin >> t;    while (t--) {        int n, k, x;        cin >> n >> k >> x;         if (x != 1) {            cout << "YES" << endl;            cout << n << endl; // We use n ones            for (int i = 0; i < n; i++) {                cout << 1 << (i == n - 1 ? "" : " ");            }            cout << endl;        }         else {            // Case where x == 1            if (n % 2 == 0 && k >= 2) {                cout << "YES" << endl;                cout << n / 2 << endl; // We use n/2 twos                for (int i = 0; i < n / 2; i++) {                    cout << 2 << (i == (n / 2) - 1 ? "" : " ");                }                cout << endl;            }             else if (n % 2 != 0 && k >= 3) {                cout << "YES" << endl;                // Use one '3' and the rest '2's                int m = 1 + (n - 3) / 2;                cout << m << endl;                cout << 3;                for (int i = 0; i < (n - 3) / 2; i++) {                    cout << " " << 2;                }                cout << endl;            }             else {                cout << "NO" << endl;            }        }    }    return 0;}