#include<iostream>#include<vector>#include<algorithm>#include<set>#define ll long longusing namespace std;int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        ll n,k;        cin>>n>>k;        vector<pair<ll,ll>>health;        for(int i=0;i<n;i++){            ll x;            cin>>x;            health.push_back({x,i+1});        }        for(int i=0;i<n;i++){            health[i].first=health[i].first%k;            if(health[i].first==0){                health[i].first=k;            }        }        sort(health.begin(),health.end(),[&] (pair<ll,ll>a,pair<ll,ll>b){            if(a.first==b.first){                return a.second<b.second;            }            return a.first>b.first;        });                for(auto it:health){            cout<<it.second<<" ";        }        cout<<"\n";    }}