#include<iostream>#include<vector>#include<algorithm>#include<cmath>#define ll long longusing namespace std;int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        ll n,k;        cin>>n>>k;        vector<ll> a(n);        for(ll i=0;i<n;i++){            cin>>a[i];        }        sort(a.begin(),a.end());        int length=0;        int mx_length=0;        for(ll i=0;i<n-1;i++){            if(a[i+1]-a[i]<=k){                length++;            }            else{                length=0;            }            mx_length=max(mx_length,length);        }        cout<<n-mx_length-1<<"\n";    }}