#include<iostream>#include<vector>#include<algorithm>#include<climits>#define ll long longusing namespace std;int main(){    int t;    cin>>t;    while(t--){        int n;        cin>>n;        vector<ll>a(n);        bool unsorted=false;        for(int i=0;i<n;i++){             cin>>a[i];                if(i>0 && a[i]<a[i-1]){                    unsorted=true;                }        }        if(unsorted){            cout<<0<<"\n";        }        else{            int minimum_op= INT_MAX;            for(int i=1;i<n;i++){                int diff= a[i]-a[i-1];                int ops= (diff/2) +1;                minimum_op= min(minimum_op, ops);            }            cout<<minimum_op<<"\n";        }    }}