#include<iostream>#include<vector>#include<algorithm>#define ll long longusing namespace std;int count(vector<ll>a){    ll n=a.size();    ll count=0;    for(int i=0;i<n-1;i++){        if(a[i]==a[i+1]){            count++;        }        else{            break;        }    }    return count;}int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        int n;        cin>>n;        vector<ll>a(n);        for(int i=0;i<n;i++) cin>>a[i];        sort(a.begin(),a.end());        if(a[0]==a[n-1]){            cout<<-1<<"\n";            continue;        }        else{            ll c=count(a);            cout<<c+1<<" "<<n-c-1<<"\n";            for(int i=0;i<=c;i++){                cout<<a[i]<<" ";            }            cout<<"\n";            for(int i=c+1;i<n;i++){                cout<<a[i]<<" ";            }        }    }}