#include<iostream>#include<vector>#include<algorithm>#include<climits>#define ll long longusing namespace std;int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        int n;        cin>>n;        ll min_1_element=INT_MAX;        ll min_2_element=INT_MAX;        ll sum=0;        while(n--){            int x;            cin>>x;            vector<ll> a(x);            for(int i=0;i<x;i++){                cin>>a[i];            }            sort(a.begin(),a.end());            sum+=a[1];            min_1_element=min(min_1_element,a[0]);            min_2_element=min(min_2_element,a[1]);        }        sum= sum-min_2_element + min_1_element;;        cout<<sum<<"\n";    }}