#include<iostream>#include<vector>#include<algorithm>#define ll long longusing namespace std;Â int main(){    ios::sync_with_stdio(false);    cin.tie(0);    int t;    cin >> t;    while(t--){        vector<vector<char>> a(10, vector<char>(10));        ll ans = 0;        for(int i=0; i<10; i++){             for(int j=0; j<10; j++){                cin >> a[i][j];                if(a[i][j] == 'X'){                    // FIX: Check from Inside (5 pts) -> Outside (1 pt)                                        if(i>=4 && i<=5 && j>=4 && j<=5){                                           ans += 5;                                        }                    else if(i>=3 && i<=6 && j>=3 && j<=6){                                          ans += 4;                                        }                    else if(i>=2 && i<=7 && j>=2 && j<=7){                                            ans += 3;                                          }                    else if(i>=1 && i<=8 && j>=1 && j<=8){                                          ans += 2;                                        }                    else { // If it's none of the above, it must be Ring 1                        ans += 1;                    }                }            }        }        cout << ans << "\n";    }    return 0;}