#include<iostream>#include<vector>#include<algorithm>#define ll long longusing namespace std;int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        ll n,p;        cin>>n>>p;        vector<ll> a(n),b(n);        for(int i=0;i<n;i++){            cin>>a[i];        }        for(int i=0;i<n;i++){            cin>>b[i];        }        vector<pair<ll,ll>>sorted;        for(int i=0;i<n;i++){            sorted.push_back({a[i],b[i]});        }        sort(sorted.begin(),sorted.end(),[](const pair<ll,ll> &x,const pair<ll,ll> &y){            return x.second<y.second;        });        vector<ll>sorted_a(n);        for(int i=0;i<n;i++){            sorted_a[i]=sorted[i].first;        }        vector<ll>sorted_b(n);        for(int i=0;i<n;i++){            sorted_b[i]=sorted[i].second;        }        if(sorted_b[0]>=p){            cout<<(ll)p * n<<"\n";            continue;        }        int no_of_elements_covered=1;        ll total_cost=p;        for(int i=0;i<n;i++){            if(no_of_elements_covered<=n){                if(sorted_b[i]<=p){                if(sorted_a[i]<n){                    ll no_of_elements_can_be_covered= min(n - no_of_elements_covered, sorted_a[i]);                    total_cost+= no_of_elements_can_be_covered * sorted_b[i];                    no_of_elements_covered+= no_of_elements_can_be_covered;                }                else{                    ll no_of_elements_can_be_covered= n - no_of_elements_covered;                    total_cost+= no_of_elements_can_be_covered * sorted_b[i];                    no_of_elements_covered+= no_of_elements_can_be_covered;                }            }            else{                total_cost+= (n - no_of_elements_covered) * p;                no_of_elements_covered=n;            }        }        }        cout<<total_cost<<"\n";    }}