#include<iostream>#include<vector>#include<algorithm>using namespace std;int main(){    ios::sync_with_stdio(false);    cin.tie(0);    int t;    cin>>t;    while(t--){        int n;        cin>>n;        vector<int>a(n-1);        int sum=0;        for(int i=0;i<n-1;i++){             cin>>a[i];            sum+=a[i];        }        int ans= sum * (-1);        cout<<ans<<'\n';    }    return 0;}