#include<iostream>#include<vector>#define ll long longusing namespace std;int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        ll n,k,x;        cin>>n>>k>>x;        ll min_sum=k*(k+1)/2;        // Using Arithmetic Progression Sum: Count * (First + Last) / 2        ll max_sum=k*(2*n - k + 1)/2;                if(x<=max_sum && x>=min_sum){            cout<<"YES\n";        }        else{            cout<<"NO\n";        }    }}