#include<iostream>#include<string>using namespace std;int main(){    int t;    cin>>t;    while(t--){        int n,m;        cin>>n>>m;        string x;        cin>>x;        string s;        cin>>s;                int ans=0;        while(x.length()<s.length()){            x+=x;            ans++;        }        if(x.find(s)!=string::npos){            cout<<ans<<endl;            continue;        }        x+=x;        ans++;        if(x.find(s)!=string::npos){            cout<<ans<<endl;        }        else{            cout<<-1<<endl;        }            }    return 0;}