#include<iostream>#include<unordered_map>#include<string>using namespace std;int main(){    int t;    cin>>t;    while(t--){        int n,k;        cin>>n>>k;        string s;        cin>>s;        unordered_map<char,int> freq;        for(char c:s){            freq[c]++;        }                        int odd_count=0;        for(auto it:freq){            if(it.second%2!=0){                odd_count++;            }        }        if(k>= odd_count - 1){            cout<<"YES\n";        }        else{            cout<<"NO\n";        }    }}