#include<iostream>#include<vector>#include<algorithm>#include<climits>#define ll long longusing namespace std;int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        int n,k;        cin>>n>>k;        vector<int> a(n);        int even_count=0;        int  min_ops=INT_MAX;         for(int i=0;i<n;i++){            cin>>a[i];            if(a[i]%k==0){                min_ops=0;            }            if(a[i]%2==0){                even_count++;            }            min_ops= min(min_ops, (k - (a[i]%k)));        }        if(k==4){            int possible_cost= INT_MAX;            if(even_count>=2){                cout<<0<<"\n";                continue;            }            if(even_count==1){                possible_cost=1;            }            if(even_count==0){                possible_cost=2;            }            cout<< min(possible_cost, min_ops) <<"\n";        }        else{            cout<< min_ops <<"\n";        }                     }}